mapper.select:
SELECT * 
FROM sys_permission a
WHERE id = #{id}

mapper.update:
UPDATE sys_permission a SET
a.isEnable = #{isEnable}
WHERE 1 = 1

    AND a.`id` in
    #{item}

mapper.select:
SELECT *
FROM sys_permission a
WHERE 1 = 1

    AND a.appId = #{appId}


    AND a.isEnable = #{isEnable}

ORDER BY a.sort DESC

mapper.delete:
DELETE FROM sys_permission

    
        WHERE 1 != 1
    
    
        WHERE `id` =
        #{id}
    
    
        WHERE `id` in
        #{id}
